import React, { useState } from 'react';

const SecurityScore = ({ onNavigate }) => {
  const [email, setEmail] = useState('');
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);

  const analyzeSecurity = async () => {
    if (!email) return;
    
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/validate-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });
      
      const data = await response.json();
      setAnalysis(data);
    } catch (error) {
      console.error('Error:', error);
    }
    setLoading(false);
  };

  const getScoreClass = (score) => {
    if (score >= 80) return 'score-excellent';
    if (score >= 60) return 'score-good';
    if (score >= 40) return 'score-fair';
    return 'score-poor';
  };

  const getScoreColor = (score) => {
    if (score >= 80) return '#10b981';
    if (score >= 60) return '#f59e0b';
    return '#ef4444';
  };

  const getScoreLabel = (score) => {
    if (score >= 80) return 'Excellent';
    if (score >= 60) return 'Good';
    if (score >= 40) return 'Fair';
    return 'Poor';
  };

  return (
    <div className="page-container">
      <div className="page-header">
        <div className="header-icon">
          <i className="fas fa-chart-line"></i>
        </div>
        <h1>Security Analysis</h1>
        <p className="page-subtitle">
          Comprehensive security scoring and detailed email analysis
        </p>
      </div>

      <div className="content-card">
        <div className="input-section">
          <div className="form-group">
            <label className="form-label">
              <i className="fas fa-envelope"></i> Enter Email for Analysis
            </label>
            <input
              type="email"
              className="form-input"
              placeholder="user@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          
          <button 
            className="btn btn-primary btn-center"
            onClick={analyzeSecurity}
            disabled={loading}
          >
            {loading ? (
              <div className="loading"></div>
            ) : (
              <i className="fas fa-chart-line"></i>
            )}
            <span>{loading ? 'Analyzing...' : 'Analyze Security'}</span>
          </button>
        </div>

        {analysis && (
          <div className="analysis-section">
            <div className="score-visualization">
              <div className={`score-circle ${getScoreClass(analysis.score)}`}
                style={{'--score-percent': `${analysis.score}%`}}>
                <div className="score-inner">
                  <span className="score-value">{analysis.score}%</span>
                  <span className="score-label">{getScoreLabel(analysis.score)}</span>
                </div>
              </div>
            </div>

            <div className="analysis-details">
              <h3 className="details-title">
                <i className="fas fa-list-alt"></i> Security Analysis
              </h3>
              
              <div className="details-grid">
                <div className="detail-card">
                  <div className="detail-icon">
                    <i className="fas fa-check-circle"></i>
                  </div>
                  <div className="detail-content">
                    <span className="detail-name">Email Status</span>
                    <span className={`detail-value ${analysis.valid ? 'value-success' : 'value-error'}`}>
                      {analysis.valid ? 'Valid' : 'Invalid'}
                    </span>
                  </div>
                </div>

                <div className="detail-card">
                  <div className="detail-icon">
                    <i className="fas fa-shield-alt"></i>
                  </div>
                  <div className="detail-content">
                    <span className="detail-name">Phishing Detection</span>
                    <span className={`detail-value ${analysis.is_phishing ? 'value-error' : 'value-success'}`}>
                      {analysis.is_phishing ? 'Detected' : 'Clean'}
                    </span>
                  </div>
                </div>

                <div className="detail-card">
                  <div className="detail-icon">
                    <i className="fas fa-globe"></i>
                  </div>
                  <div className="detail-content">
                    <span className="detail-name">Domain</span>
                    <span className="detail-value">{analysis.domain}</span>
                  </div>
                </div>

                <div className="detail-card">
                  <div className="detail-icon">
                    <i className="fas fa-server"></i>
                  </div>
                  <div className="detail-content">
                    <span className="detail-name">Email Service</span>
                    <span className={`detail-value ${analysis.mx_valid ? 'value-success' : 'value-warning'}`}>
                      {analysis.mx_valid ? 'Configured' : 'Not Configured'}
                    </span>
                  </div>
                </div>
              </div>
            </div>

            {analysis.recommendations && analysis.recommendations.length > 0 && (
              <div className="recommendations-section">
                <h3 className="recommendations-title">
                  <i className="fas fa-lightbulb"></i> Security Recommendations
                </h3>
                <div className="recommendations-grid">
                  {analysis.recommendations.map((rec, index) => (
                    <div key={index} className="recommendation-card">
                      <i className="fas fa-chevron-right"></i>
                      <span>{rec}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            <div className="social-integration">
              <h3 className="social-title">
                <i className="fas fa-share-alt"></i> Social Media Integration
              </h3>
              <p className="social-subtitle">Linked accounts and online presence analysis</p>
              <div className="social-links">
                <a href="#" className="social-link">
                  <i className="fab fa-linkedin"></i>
                  <span>LinkedIn</span>
                </a>
                <a href="#" className="social-link">
                  <i className="fab fa-twitter"></i>
                  <span>Twitter</span>
                </a>
                <a href="#" className="social-link">
                  <i className="fab fa-github"></i>
                  <span>GitHub</span>
                </a>
                <a href="#" className="social-link">
                  <i className="fab fa-facebook"></i>
                  <span>Facebook</span>
                </a>
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="page-actions">
        <button 
          className="btn btn-secondary"
          onClick={() => onNavigate('home')}
        >
          <i className="fas fa-arrow-left"></i>
          <span>Back to Home</span>
        </button>
      </div>
    </div>
  );
};

export default SecurityScore;
