import React, { useState } from 'react';

const PhishingDetector = ({ onNavigate }) => {
  const [url, setUrl] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const detectPhishing = async () => {
    if (!url) return;
    
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/detect-phishing', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url }),
      });
      
      const data = await response.json();
      setResult(data);
    } catch (error) {
      console.error('Error:', error);
      setResult({
        is_phishing: false,
        risk_score: 0,
        warnings: ['Error analyzing URL'],
        domain: 'unknown'
      });
    }
    setLoading(false);
  };

  return (
    <div className="phishing-detector">
      <div className="card">
        <h1><i className="fas fa-fish"></i> Phishing URL Detector</h1>
        <p>Enter a URL to check if it's a potential phishing attempt</p>
        
        <div className="form-group">
          <label className="form-label">URL to Analyze</label>
          <input
            type="text"
            className="form-input"
            placeholder="https://example.com"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
        </div>
        
        <button 
          className="btn btn-primary"
          onClick={detectPhishing}
          disabled={loading}
        >
          {loading ? <div className="loading"></div> : <i className="fas fa-search"></i>}
          {loading ? ' Analyzing...' : ' Detect Phishing'}
        </button>

        {result && (
          <div className={`result-container ${result.is_phishing ? 'result-invalid' : 'result-valid'}`}>
            <div className="result-icon">
              {result.is_phishing ? (
                <i className="fas fa-skull-crossbones" style={{color: '#dc2626'}}></i>
              ) : (
                <i className="fas fa-shield-check" style={{color: '#10b981'}}></i>
              )}
            </div>
            
            <h3>
              {result.is_phishing ? 'Phishing Detected!' : 'URL appears safe'}
            </h3>
            
            <p>Risk Score: {result.risk_score}%</p>
            <p>Domain: {result.domain}</p>
            
            {result.warnings.length > 0 && (
              <div>
                <h4>Warnings:</h4>
                <ul>
                  {result.warnings.map((warning, index) => (
                    <li key={index}>{warning}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}
      </div>
      
      <button 
        className="btn btn-secondary"
        onClick={() => onNavigate('home')}
        style={{marginTop: '2rem'}}
      >
        <i className="fas fa-arrow-left"></i> Back to Home
      </button>
    </div>
  );
};

export default PhishingDetector;
