import React, { useState } from 'react';

const EmailValidator = ({ onNavigate }) => {
  const [email, setEmail] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [recentEmails, setRecentEmails] = useState([]);

  const validateEmail = async () => {
    if (!email) return;
    
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/validate-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });
      
      const data = await response.json();
      setResult(data);
      
      // Add to recent emails
      setRecentEmails(prev => [data, ...prev.slice(0, 4)]);
    } catch (error) {
      console.error('Error:', error);
      setResult({
        valid: false,
        message: 'Error validating email',
        score: 0,
        is_phishing: false
      });
    }
    setLoading(false);
  };

  const getScoreColor = (score) => {
    if (score >= 80) return '#10b981';
    if (score >= 60) return '#f59e0b';
    return '#dc2626';
  };

  const getScoreLevel = (score) => {
    if (score >= 80) return 'Excellent';
    if (score >= 60) return 'Good';
    if (score >= 40) return 'Fair';
    return 'Poor';
  };

  return (
    <div className="email-validator">
      <div className="card">
        <h1><i className="fas fa-envelope"></i> Email Validator</h1>
        <p>Validate email format, domain authenticity, and detect phishing attempts</p>
        
        <div className="form-group">
          <label className="form-label">Email Address</label>
          <input
            type="email"
            className="form-input"
            placeholder="user@example.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <small style={{color: 'var(--text-secondary)', marginTop: '0.5rem', display: 'block'}}>
            Try: user@gmail.com, admin@atria.edu.com, or test@gmail-security-verify.com
          </small>
        </div>
        
        <button 
          className="btn btn-primary"
          onClick={validateEmail}
          disabled={loading}
        >
          {loading ? <div className="loading"></div> : <i className="fas fa-check-circle"></i>}
          {loading ? ' Validating...' : ' Validate Email'}
        </button>

        {result && (
          <div className={`result-container ${
            result.is_phishing ? 'result-invalid' : 
            result.valid ? 'result-valid' : 'result-warning'
          }`}>
            <div className="result-icon">
              {result.is_phishing ? (
                <i className="fas fa-skull-crossbones" style={{color: '#dc2626', fontSize: '3rem'}}></i>
              ) : result.valid ? (
                <i className="fas fa-check-circle" style={{color: '#10b981', fontSize: '3rem'}}></i>
              ) : (
                <i className="fas fa-exclamation-triangle" style={{color: '#f59e0b', fontSize: '3rem'}}></i>
              )}
            </div>
            
            <h3 style={{
              color: result.is_phishing ? '#dc2626' : 
                     result.valid ? '#10b981' : '#f59e0b'
            }}>
              {result.is_phishing ? 'üö® PHISHING DETECTED!' : 
               result.valid ? '‚úÖ Valid Email' : '‚ö†Ô∏è Validation Warning'}
            </h3>
            
            <p><strong>{result.message}</strong></p>
            
            {!result.is_phishing && (
              <div className="score-section" style={{margin: '1rem 0'}}>
                <div className="score-display" style={{
                  backgroundColor: getScoreColor(result.score),
                  color: 'white',
                  padding: '0.5rem 1rem',
                  borderRadius: '0.5rem',
                  display: 'inline-block',
                  fontWeight: 'bold'
                }}>
                  Security Score: {result.score}% - {getScoreLevel(result.score)}
                </div>
              </div>
            )}
            
            <p><strong>Domain:</strong> {result.domain}</p>
            {result.domain_type && <p><strong>Domain Type:</strong> {result.domain_type}</p>}
            {result.mx_valid !== undefined && (
              <p><strong>Email Service:</strong> {result.mx_valid ? '‚úÖ Configured' : '‚ö†Ô∏è Not Configured'}</p>
            )}
            
            {result.recommendations && result.recommendations.length > 0 && (
              <div style={{marginTop: '1rem'}}>
                <h4>Recommendations:</h4>
                <ul>
                  {result.recommendations.map((rec, index) => (
                    <li key={index} style={{marginBottom: '0.5rem'}}>
                      {rec.startsWith('üö®') || rec.startsWith('‚ùå') ? (
                        <i className="fas fa-exclamation-circle" style={{color: '#dc2626'}}></i>
                      ) : rec.startsWith('‚ö†Ô∏è') ? (
                        <i className="fas fa-exclamation-triangle" style={{color: '#f59e0b'}}></i>
                      ) : (
                        <i className="fas fa-info-circle" style={{color: '#3b82f6'}}></i>
                      )}
                      {' '}{rec}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}

        {recentEmails.length > 0 && (
          <div className="recent-analysis" style={{marginTop: '2rem'}}>
            <h4>Recent Validations</h4>
            <div className="recent-list">
              {recentEmails.map((item, index) => (
                <div key={index} className="recent-item" style={{
                  padding: '0.5rem',
                  margin: '0.5rem 0',
                  borderLeft: `4px solid ${
                    item.is_phishing ? '#dc2626' : 
                    item.valid ? '#10b981' : '#f59e0b'
                  }`,
                  backgroundColor: 'var(--secondary-bg)'
                }}>
                  <div style={{fontSize: '0.9rem', color: 'var(--text-secondary)'}}>
                    {item.domain} - Score: {item.score}% - {item.valid ? 'Valid' : 'Invalid'}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
      
      <button 
        className="btn btn-secondary"
        onClick={() => onNavigate('home')}
        style={{marginTop: '2rem'}}
      >
        <i className="fas fa-arrow-left"></i> Back to Home
      </button>
    </div>
  );
};

export default EmailValidator;
