import React, { useState } from 'react';

const EmailValidator = ({ onNavigate }) => {
  const [email, setEmail] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [recentEmails, setRecentEmails] = useState([]);

  const validateEmail = async () => {
    if (!email) return;
    
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/validate-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });
      
      const data = await response.json();
      setResult(data);
      
      // Add to recent emails
      setRecentEmails(prev => [data, ...prev.slice(0, 4)]);
    } catch (error) {
      console.error('Error:', error);
      setResult({
        valid: false,
        message: 'Error validating email',
        score: 0,
        is_phishing: false
      });
    }
    setLoading(false);
  };

  const getScoreColor = (score) => {
    if (score >= 80) return '#10b981';
    if (score >= 60) return '#f59e0b';
    return '#ef4444';
  };

  const getScoreLevel = (score) => {
    if (score >= 80) return 'Excellent';
    if (score >= 60) return 'Good';
    if (score >= 40) return 'Fair';
    return 'Poor';
  };

  const getStatusIcon = (result) => {
    if (result.is_phishing) return 'fas fa-skull-crossbones';
    if (result.valid) return 'fas fa-check-circle';
    return 'fas fa-exclamation-triangle';
  };

  const getStatusColor = (result) => {
    if (result.is_phishing) return '#ef4444';
    if (result.valid) return '#10b981';
    return '#f59e0b';
  };

  const getStatusText = (result) => {
    if (result.is_phishing) return 'Phishing Detected';
    if (result.valid) return 'Valid Email';
    return 'Validation Warning';
  };

  return (
    <div className="email-validator">
      <div className="card">
        <h1>
          <i className="fas fa-envelope"></i>
          Email Validator
        </h1>
        <p>Validate email format, domain authenticity, and detect phishing attempts in real-time</p>
        
        <div className="form-group">
          <label className="form-label">
            <i className="fas fa-envelope"></i> Email Address
          </label>
          <input
            type="email"
            className="form-input"
            placeholder="user@example.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <small style={{color: 'var(--text-muted)', marginTop: '0.5rem', display: 'block'}}>
            <i className="fas fa-lightbulb"></i> Try: user@gmail.com, admin@atria.edu.com, or test@gmail-security-verify.com
          </small>
        </div>
        
        <button 
          className="btn btn-primary"
          onClick={validateEmail}
          disabled={loading}
        >
          {loading ? <div className="loading"></div> : <i className="fas fa-check-circle"></i>}
          {loading ? ' Validating Email...' : ' Validate Email'}
        </button>

        {result && (
          <div className={`result-container ${
            result.is_phishing ? 'result-invalid' : 
            result.valid ? 'result-valid' : 'result-warning'
          }`}>
            <div className="result-icon">
              <i 
                className={getStatusIcon(result)} 
                style={{color: getStatusColor(result)}}
              ></i>
            </div>
            
            <h3 style={{
              color: getStatusColor(result),
              marginBottom: '1rem',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem'
            }}>
              <i className={getStatusIcon(result)}></i>
              {getStatusText(result)}
            </h3>
            
            <p style={{fontSize: '1.1rem', marginBottom: '1.5rem'}}>
              <strong>{result.message}</strong>
            </p>
            
            {!result.is_phishing && (
              <div className="score-section" style={{margin: '1.5rem 0'}}>
                <div className="score-display" style={{
                  backgroundColor: getScoreColor(result.score),
                  color: 'white',
                  padding: '0.75rem 1.5rem',
                  borderRadius: '0.75rem',
                  display: 'inline-flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  fontWeight: 'bold',
                  fontSize: '1.1rem'
                }}>
                  <i className="fas fa-shield-alt"></i>
                  Security Score: {result.score}% - {getScoreLevel(result.score)}
                </div>
              </div>
            )}
            
            <div className="analysis-details">
              <div className="detail-item">
                <strong><i className="fas fa-globe"></i> Domain:</strong>
                <span>{result.domain}</span>
              </div>
              
              {result.domain_type && (
                <div className="detail-item">
                  <strong><i className="fas fa-tag"></i> Domain Type:</strong>
                  <span style={{
                    color: result.domain_type === 'trusted' ? '#10b981' : 
                           result.domain_type === 'unknown' ? '#f59e0b' : '#ef4444',
                    fontWeight: '600'
                  }}>
                    {result.domain_type.charAt(0).toUpperCase() + result.domain_type.slice(1)}
                  </span>
                </div>
              )}
              
              {result.mx_valid !== undefined && (
                <div className="detail-item">
                  <strong><i className="fas fa-server"></i> Email Service:</strong>
                  <span style={{
                    color: result.mx_valid ? '#10b981' : '#f59e0b',
                    fontWeight: '600'
                  }}>
                    <i className={result.mx_valid ? 'fas fa-check' : 'fas fa-exclamation-triangle'}></i>
                    {' '}{result.mx_valid ? 'Properly Configured' : 'Needs Configuration'}
                  </span>
                </div>
              )}
            </div>
            
            {result.recommendations && result.recommendations.length > 0 && (
              <div style={{marginTop: '1.5rem'}}>
                <h4 style={{marginBottom: '1rem', color: 'var(--accent-blue)'}}>
                  <i className="fas fa-lightbulb"></i> Recommendations
                </h4>
                <ul>
                  {result.recommendations.map((rec, index) => (
                    <li key={index} style={{
                      marginBottom: '0.75rem', 
                      padding: '0.75rem',
                      background: 'var(--card-bg)',
                      borderRadius: '0.5rem',
                      borderLeft: '3px solid var(--accent-blue)',
                      display: 'flex',
                      alignItems: 'flex-start',
                      gap: '0.5rem'
                    }}>
                      <i className="fas fa-chevron-right" style={{color: 'var(--accent-blue)', marginTop: '0.25rem'}}></i>
                      {rec}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}

        {recentEmails.length > 0 && (
          <div className="recent-analysis">
            <h4 style={{marginBottom: '1rem'}}>
              <i className="fas fa-history"></i> Recent Validations
            </h4>
            <div className="recent-list">
              {recentEmails.map((item, index) => (
                <div key={index} className="recent-item">
                  <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
                    <span style={{color: 'var(--text-secondary)', fontSize: '0.9rem'}}>
                      <i className="fas fa-envelope"></i> {item.domain}
                    </span>
                    <span style={{
                      color: getScoreColor(item.score),
                      fontWeight: '600',
                      fontSize: '0.85rem'
                    }}>
                      <i className="fas fa-shield-alt"></i> {item.score}%
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
      
      <button 
        className="btn btn-secondary"
        onClick={() => onNavigate('home')}
        style={{marginTop: '2rem'}}
      >
        <i className="fas fa-arrow-left"></i> Back to Home
      </button>
    </div>
  );
};

export default EmailValidator;
