import React, { useState } from 'react';

const EmailValidator = ({ onNavigate }) => {
  const [email, setEmail] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const validateEmail = async () => {
    if (!email) return;
    
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/validate-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });
      
      const data = await response.json();
      setResult(data);
    } catch (error) {
      console.error('Error:', error);
      setResult({
        valid: false,
        message: 'Error validating email',
        score: 0
      });
    }
    setLoading(false);
  };

  return (
    <div className="email-validator">
      <div className="card">
        <h1><i className="fas fa-envelope"></i> Email Validator</h1>
        <p>Validate email format, domain authenticity, and detect phishing attempts</p>
        
        <div className="form-group">
          <label className="form-label">Email Address</label>
          <input
            type="email"
            className="form-input"
            placeholder="user@example.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        
        <button 
          className="btn btn-primary"
          onClick={validateEmail}
          disabled={loading}
        >
          {loading ? <div className="loading"></div> : <i className="fas fa-check-circle"></i>}
          {loading ? ' Validating...' : ' Validate Email'}
        </button>

        {result && (
          <div className={`result-container ${
            result.is_phishing ? 'result-invalid' : 
            result.valid ? 'result-valid' : 'result-warning'
          }`}>
            <div className="result-icon">
              {result.is_phishing ? (
                <i className="fas fa-times-circle" style={{color: '#dc2626'}}></i>
              ) : result.valid ? (
                <i className="fas fa-check-circle" style={{color: '#10b981'}}></i>
              ) : (
                <i className="fas fa-exclamation-triangle" style={{color: '#f59e0b'}}></i>
              )}
            </div>
            
            <h3>
              {result.is_phishing ? 'Phishing Email Detected!' : 
               result.valid ? 'Valid Email' : 'Validation Warning'}
            </h3>
            
            <p>{result.message}</p>
            <p>Security Score: {result.score}%</p>
            {result.domain && <p>Domain: {result.domain}</p>}
            
            {result.recommendations && result.recommendations.length > 0 && (
              <div>
                <h4>Recommendations:</h4>
                <ul>
                  {result.recommendations.map((rec, index) => (
                    <li key={index}>{rec}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}
      </div>
      
      <button 
        className="btn btn-secondary"
        onClick={() => onNavigate('home')}
        style={{marginTop: '2rem'}}
      >
        <i className="fas fa-arrow-left"></i> Back to Home
      </button>
    </div>
  );
};

export default EmailValidator;
